import React, { Component } from 'react'
import Cards from './Cards'
export default class Marble extends Component {
    state={
        userId : '',
        drug:[],
        startDate: new Date(),
        endDate: new Date(),
        search: '',

      }
      updateSearch =({target:{value , name}})=>{

        this.setState({[name]: value});

      }
      ;
      componentDidMount(){
        axios.get('http://localhost:4000/file',{
          headers: {
            token:  localStorage.usertoken//the token is a variable which holds the token
          }
         }).then(res =>{
          //  console.log(res)
           console.log("hla")

            this.setState({ drug: res.data.drugs });

            console.log(this.state.drug);
          })
        }

  render() {
    let filteredTeams = this.props.team.filter(
      (item) => {
        return item.team_name.toLowerCase().indexOf(this.state.search.toLowerCase()) !== -1;
      }

    );
    console.log(this.state);
    var teams = filteredTeams.map( item => {

      return <Cards teamName={item.team_name} badgeImg={item.team_badge}/>
    })
    return (
      <div>
        <div className = "search">
      <input type= "text" 
      name="search"
      onChange={this.updateSearch}
      />
      </div>
      <div className = "app">
        {this.props.team.length  > 1 ? teams : <Circle4 />}
        {/* {teams} */}
      </div>

      </div>

    )
  }
}
